import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import "bootstrap/dist/js/bootstrap.min.js";
import { FolderRow } from './components/folderRow';
import { getFolders } from './api/folder'

function App() {
  const folders = getFolders();

  console.log(folders)

  return (
    <>
      <nav id="header" className="site-header sticky-top py-1 bg-dark">
          <div className="container d-flex flex-column flex-sm-row justify-content-center">
              <h4 className="text-light" >Agustina Hirschfeld</h4>
          </div>
      </nav>

      <div className="container" style={{marginTop: "2em"}}>

        <div id="addFolder" class="input-group mb-2" style={{width: "50%", margin: "auto 0 2em auto"}}>
          <input id="input-newFolder" type="text" class="form-control" placeholder="Ingresar nombre de carpeta" aria-label="" aria-describedby="basic-addon2"/>
          <div class="input-group-append">
            <button id="btn-newFolder" class="btn btn-secondary" type="button">Add</button>
          </div>
        </div>

        <table className="table table-striped">
          <thead className="thead-dark">
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nombre</th>
              <th scope="col">Contenido</th>
              <th scope="col"></th>
            </tr>
          </thead>

          <tbody class="accordion accordion-flush" id="accordionExample">
              {
                (folders) ? 
                  showFolders(folders)
                : null
              }
          </tbody>
        </table>
      </div>

    </>
  );
}

function showFolders(folders) {
  return (
    folders.map((folder, i) => {
      return (
        showFolder(folder, i+1)
      )
    })
  )
}

function showFolder(folder, id){
  return (
    <FolderRow id={id} name={folder.name} todoList={folder.todoList}/>
  )
}


export default App;
