import React from 'react'
import { updateToDo } from '../api/todo';

export const ToDoRow = ({todo}) => {
    return (
        <tr>
            <th className="align-middle" scope="row">
                <div className="form-check">
                    {
                        todo.done ? 
                            <input 
                                className="form-check-input" 
                                type="checkbox" value="" 
                                id={"todoDoneCheck" + todo.id}
                                checked={todo.done}
                                onClick={() => updateTodo}
                                checked={this.props.checked} onChange={()=>{this.props.checked = !this.props.checked}}
                            /> :
                            <input 
                                className="form-check-input" 
                                type="checkbox" value="" 
                                id={"todoDoneCheck" + todo.id}
                                checked={todo.done}
                                onClick={() => updateTodo} 
                            />
                    }
                </div>
            </th>
            <td className="align-middle">{todo.task}</td>
            <td className="align-middle flex-row" width="250px" >
                <button 
                    onClick={() => editToDo(todo)}
                    type="button" className="btn btn-outline-success"
                    style={{width: "5em", height: "2.5em"}}
                    data-bs-toggle="modal" data-bs-target="#exampleModal"
                >
                    Edit
                </button>
            </td>
        </tr>
    )
}

function updateTodo(todo){
    todo.done = true
    updateToDo(todo);
}

function editToDo(todo) {
    document.getElementById('exampleModalLabel').innerHTML = `Editing task: "${todo.task}"`;
    const buttonSave = document.getElementById('saveChangesToDo');
    buttonSave.onclick = () => {
        let newTask = document.getElementById('input-editTask').value
        if(newTask.trim() !== ""){
            todo.task = newTask;
            updateToDo(todo)
            window.location.reload();
        }
    }
}
