import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FolderRow } from './components/folderRow';

function App() {
  const folders = [
    {
      name: "carpeta1",
      todoList: [
        {
          name: "todo1"
        },
        {
          name: "todo2"
        }
      ]
    }
  ]

  return (
    <>
      <nav id="header" classNameName="site-header sticky-top py-1 bg-dark">
          <div classNameName="container d-flex flex-column flex-sm-row justify-content-center">
              <h4 classNameName="text-light" >Agustina Hirschfeld</h4>
          </div>
      </nav>
      <div classNameName="container" style={{marginTop: "2em"}}>
        <table className="table">
          <thead className="thead-dark">
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nombre</th>
              <th scope="col">Contenido</th>
              <th scope="col"></th>
            </tr>
          </thead>

          <tbody>
              {
                showFolders(folders)
              }
          </tbody>
        </table>
      </div>

    </>
  );
}

const showFolders = ({folders}) => {
  return (
    folders.map((folder, i) => {
      showFolder(folder, i);
    })
  )
}

const showFolder = ({folder, id}) => {
  return (
    <FolderRow id={id} name={folder.name} todoList={folder.todoList}/>
  )
}


export default App;
