import React from 'react';
import { ToDoRow } from "./todoRow";

export const FolderRow = ({id, name, todoList}) => {
    let today = new Date();
    let date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
    var hour = today.getHours() + ':' + today.getMinutes();

    return (
        <>
            <tr>
                <th class="align-middle" scope="row">{id}</th>
                <td class="align-middle">{name}</td>
                <td class="align-middle">{todoList.length}</td>
                <td class="align-middle">{date + " " + hour}</td>
                <td class="align-middle flex-row" width="250px" >
                    <button 
                        className="btn btn-outline-primary " 
                        style={{width: "5em", height: "2.5em", justifyContent: "center", marginRight: ".5em"}} 
                        type="button" data-bs-toggle="collapse" data-bs-target={"#" + name + id + ""}
                        aria-expanded="true" aria-controls={name + id + ""} 
                    >
                        View
                    </button>
                    <button 
                          
                        type="button" className="btn btn-outline-danger"
                        style={{width: "5em", height: "2.5em"}}
                    >
                        Remove
                    </button>
                </td>
            </tr>
            <tr colSpan="4">
                <td id={name + id + ""} class="accordion-collapse collapse" aria-labelledby="headingOne" data-bs-parent="#accordionExample" colSpan="5">
                    <div className="container" style={{paddingLeft: "3em", paddingRight: "30em"}}>
                        <table className="table" width="50%">
                            <tbody>
                                {
                                    showToDos(todoList)
                                }
                            </tbody>
                        </table>
                        <div id="addFolder" class="input-group mb-2" style={{width: "50%", margin: "auto 0 2em auto"}}>
                            <input id="input-newFolder" type="text" class="form-control" placeholder="Ingresar nombre de carpeta" aria-label="" aria-describedby="basic-addon2"/>
                            <div class="input-group-append">
                                <button id="btn-newFolder" class="btn btn-secondary" type="button">Add</button>
                            </div>
                        </div>
                    </div>
                </td>
            </tr>
        </>
    )
}

function showToDos(todoList) {
    return (
        todoList.map((todo) => {
            return (
            showToDo(todo)
            )
        })
    )
}

function showToDo(todo){
    return (
      <ToDoRow name={todo.name}/>
    )
}
