import React from 'react';
import { ToDoRow } from "./todoRow";

export const FolderRow = ({id, name, todosList}) => {
    let today = new Date();
    let date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
    var hour = today.getHours() + ':' + today.getMinutes();

    return (
        <>
            <tr>
                <th className="align-middle" scope="row">{id}</th>
                <td className="align-middle">{name}</td>
                <td className="align-middle">{todosList.length}</td>
                <td className="align-middle">{date + " " + hour}</td>
                <td className="align-middle flex-row" width="250px" >
                    <button 
                        className="btn btn-outline-primary " 
                        style={{width: "5em", height: "2.5em", justifyContent: "center", marginRight: ".5em"}} 
                        type="button" data-bs-toggle="collapse" data-bs-target={"#" + "carpeta" + id + ""}
                        aria-expanded="true" aria-controls={"carpeta" + id + ""} 
                    >
                        View
                    </button>
                    <button 
                          
                        type="button" className="btn btn-outline-danger"
                        style={{width: "5em", height: "2.5em"}}
                    >
                        Remove
                    </button>
                </td>
            </tr>
            <tr colSpan="4">
                <td id={"carpeta" + id + ""} className="accordion-collapse collapse" aria-labelledby="headingOne" data-bs-parent="#accordionExample" colSpan="5">
                    <div className="container" style={{paddingLeft: "3em", paddingRight: "30em"}}>
                        <div id="addFolder" className="input-group mb-2" style={{width: "40%", margin: "auto auto 2em 0"}}>
                            <input id="input-newFolder" type="text" className="form-control" placeholder="Ingresar nombre de carpeta" aria-label="" aria-describedby="basic-addon2"/>
                            <div className="input-group-append">
                                <button id="btn-newFolder" className="btn btn-secondary" type="button">Add</button>
                            </div>
                        </div>
                        <table className="table" width="50%">
                            <tbody>
                                {
                                    showToDos(todosList)
                                }
                            </tbody>
                        </table>
                    </div>
                </td>
            </tr>
        </>
    )
}

function showToDos(todosList) {
    return (
        todosList.map((todo, i) => {
            return (
                showToDo(todo, i)
            )
        })
    )
}

function showToDo(todo, key){
    return (
      <ToDoRow key={key} name={todo.task}/>
    )
}
