import React from 'react'
import { updateToDo } from '../api/todo';

export const ToDoRow = ({todo}) => {
    return (
        <tr>
            <th className="align-middle" scope="row">
                <div className="form-check">
                    <input onClick={() => updateTodo} className="form-check-input" type="checkbox" value="" id={"todoDoneCheck" + todo.id} checked={todo.done} onChange={null}/>
                </div>
            </th>
            <td className="align-middle">{todo.task}</td>
            <td className="align-middle flex-row" width="250px" >
                <button 
                        
                    type="button" className="btn btn-outline-success"
                    style={{width: "5em", height: "2.5em"}}
                >
                    Edit
                </button>
            </td>
        </tr>
    )
}

function updateTodo(todo){
    if(document.querySelector(`#todoDoneCheck${todo.id}`).checked){
        todo.done = true;
    }else{
        todo.done = false;
    }
    updateToDo(todo);

}
