import React from 'react'

export const FolderRow = ({id, name, todoList}) => {
    let today = new Date();
    let date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
    var hour = today.getHours() + ':' + today.getMinutes();

    return (
        <>
            <tr>
                <th class="align-middle" scope="row">{id}</th>
                <td class="align-middle">{name}</td>
                <td class="align-middle">{todoList.length}</td>
                <td class="align-middle">{date + " " + hour}</td>
                <td class="align-middle flex-row" width="250px" >
                    <button 
                        className="btn btn-outline-primary " 
                        style={{width: "5em", height: "3em", justifyContent: "center", marginRight: ".5em"}} 
                        type="button" data-bs-toggle="collapse" data-bs-target={"#" + name + id + ""}
                        aria-expanded="true" aria-controls={name + id + ""} 
                    >
                        View
                    </button>
                    <button 
                        style={{width: "5em", height: "3em", justifyContent: "center"}}  
                        type="button" className="btn btn-outline-danger"
                    >
                        Remove
                    </button>
                </td>
            </tr>
            <tr colSpan="4">
                <td id={name + id + ""} class="accordion-collapse collapse" aria-labelledby="headingOne" data-bs-parent="#accordionExample" colSpan="5">
                    <tbody>
                        {
                            showToDos(todoList)
                        }
                    </tbody>
                </td>
            </tr>
        </>
    )
}

